    public void setExecutorUnitCode(Integer executorUnitCode) {
        this.executorUnitCode = executorUnitCode;
    }

    public void setYear(Integer year) {
        this.year = year;
    }

    public void setPeriod(Integer period) {
        this.period = period;
    }

    public void setEntryCodList(List<String> entryCodList) {
        this.entryCodList = entryCodList;
    }


    private String composeEntryCodeFilters() {
        String sql = "";
        if (entryCodList != null && !entryCodList.isEmpty()) {
            sql += "     AND (";
            for (int i = 0; i < entryCodList.size(); i++) {
                if (i != 0) {
                    sql += " OR ";
                }
                sql += " CU.RBR_COD LIKE '" + entryCodList.get(i) + "'";
            }
            sql += ") \n";
        }
        return sql;
    }

    private String composeCategoryCodeFilters() {
        String sql = "";
        if (categoryCodList != null && !categoryCodList.isEmpty()) {
            sql += "     AND (";
            for (int i = 0; i < categoryCodList.size(); i++) {
                if (i != 0) {
                    sql += " OR ";
                }
                sql += " CU.CAT_COD LIKE '" + categoryCodList.get(i) + "'";
            }
            sql += ") \n";
        }
        return sql;
    }


    public void setCategoryCodList(List<String> categoryCodList) {
        this.categoryCodList = categoryCodList;
    }

    private String setFilters() {
        String sql = "";
        if (null != executorUnitCode) {
            sql += " AND CXP.UNIDAD_ACAD_ADM = " + executorUnitCode + "\n"; //sede
        }

        if (null != year) {
            sql += " AND TO_CHAR(CXP.GESTION) = '" + year + "'\n";
        }

        if (null != period) {
            sql += " AND TO_CHAR(CXP.PERIODO) = '" + period + "'\n";
        }

        if (null != entryCodList) {
            sql += composeEntryCodeFilters();
        }

        if (null != categoryCodList) {
            sql += composeCategoryCodeFilters();
        }

        return sql;
    }
